import { API_URL } from './utils';

export const createTask = async (taskobj) => {
    const url = API_URL;
    const option = {
        method: "POST",
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskobj)
    };
    try {
        const result = await fetch(url, option);
        const data = await result.json();
        return data;
    } catch (error) {
        return error;
    }
}
