import react, { useState } from 'react';
import { FaCheck, FaPenAlt, FaPlus, FaSearch, FaTrash } from "react-icons/fa";
import {ToastContainer} from 'react-toastify';

function TaskManager() {

const [input, setInput]=useState('');

const handleAddTask=()=>{
    const obj={
        taskName:input,
        isDone:false
    }
    console.log(obj);
    
}

    return (
        <div className='d-flex flex-column align-items-center w-50 m-auto mt-5'>
            <h1 className='mb-4'>Task Manager</h1>

            {/* Input and Search Box */}

            <div className='d-flex justify-content-between align-items-center mb-4 w-100'>
                <div className='input-group flex-grow-1 me-2'>
                    <input value={input} onChange={(e)=>setInput(e.target.value)} type="text" placeholder='Add a new Task' className='form-control mw-1' />
                    <button className='btn btn-success btn-sm me-2'>
                        <FaPlus className='m-2' />
                    </button>
                </div>
                <div className='input-group flex-grow-1'>
                    <span className='input-group-text'>
                        <FaSearch />
                    </span>
                    <input type="text" placeholder='Search Task' className='form-control mw-1' />
                </div>
            </div>

            {/* Lists of items */}

            <div className='d-flex flex-column w-100'>
                <div className="m-2 p-2 border bg-light w-100 rounded-3 d-flex justify-content-between align-items-center">
                    <span className='text-decoration-line-through'>First task</span>
                    <div>
                        <button onClick={handleAddTask} className='btn btn-success btn-sm me-2' type='button'>
                            <FaCheck />
                        </button>
                        <button className='btn btn-primary btn-sm me-2' type='button'>
                            <FaPenAlt />
                        </button>
                        <button className='btn btn-danger btn-sm me-2' type='button'>
                            <FaTrash />
                        </button>
                    </div>
                </div>
            </div>

            {/* Toastify */}

            <ToastContainer position='top-right' autoClose={3000} hideProgressBar={false}/>
        </div>
    )
}
export default TaskManager;
