import { useEffect, useState } from 'react';
import { FaCheck, FaPenAlt, FaPlus, FaSearch, FaTrash } from "react-icons/fa";
import { toast, ToastContainer } from 'react-toastify';
import { createTask, DeleteTaskById, getAllTaks, UpdateTaskById } from './api';

function TaskManager() {

    const [input, setInput] = useState('');
    const [tasks, setTasks] = useState([]);
    const [copyTask, setCopyTask] = useState([]);
    const [updateTask, setUpdateTask] = useState(null);

    const handleTask = () => {
        if (updateTask && input) {
            // update api call
            const obj = {
                taskName: input,
                isDone: updateTask.isDone,
                _id: updateTask._id
            }
            handleUpdate(obj);

        } else if (updateTask === null && input) {
            // Create api call

            handleAddTask();
        }
        setInput('');
    }

    useEffect(() => {
        if (updateTask) {
            setInput(updateTask.taskName);
        }
    }, [updateTask]);

    const handleAddTask = async () => {
        const obj = {
            taskName: input,
            isDone: false
        }
        try {
            const { success, message } = await createTask(obj);
            if (success) {
                // show succes toast
                toast(message, 'success');
            } else {
                // show error toast
                toast(message, 'error');
            }
            fetchAllTasks();

        } catch (error) {
            console.error(error);
            toast("Failed to Create to Task", 'error');
        }

    }

    const fetchAllTasks = async () => {
        try {
            const { data } = await getAllTaks();
            setTasks(data);
            setCopyTask(data);

        } catch (error) {
            console.error(error);
            toast("Failed to Create to Task", 'error');
        }
    }
    useEffect(() => {
        fetchAllTasks();
    }, []);

    const handleDeleteTask = async (id) => {
        try {
            const { success, message } = await DeleteTaskById(id)
            if (success) {
                // show succes toast
                toast(message, 'success');
            } else {
                // show error toast
                toast(message, 'error');
            }
            fetchAllTasks();

        } catch (error) {
            console.error(error);
            toast("Failed to Create to Task", 'error');
        }
    }

    const handleCheckAndUncheck = async (item) => {
        const { _id, isDone, taskName } = item;
        const obj = {
            taskName,
            isDone: !isDone
        }
        try {
            const { success, message } = await UpdateTaskById(_id, obj)
            if (success) {
                // show succes toast
                toast(message, 'success');
            } else {
                // show error toast
                toast(message, 'error');
            }
            fetchAllTasks();

        } catch (error) {
            console.error(error);
            toast("Failed to Create to Task", 'error');
        }
    }

    const handleUpdate = async (item) => {
        const { _id, isDone, taskName } = item;
        const obj = {
            taskName,
            isDone: isDone
        }
        try {
            const { success, message } = await UpdateTaskById(_id, obj)
            if (success) {
                // show succes toast
                toast(message, 'success');
            } else {
                // show error toast
                toast(message, 'error');
            }
            fetchAllTasks();

        } catch (error) {
            console.error(error);
            toast("Failed to Create to Task", 'error');
        }
    }

    const handleSearch = (e) => {
        const term = e.target.value.toLowerCase();
        const oldTasks = [...copyTask];
        const results = oldTasks.filter((item) => item.taskName.toLowerCase().includes(term));
        setTasks(results);
    }

    return (
        <div className='d-flex flex-column align-items-center w-50 m-auto mt-5'>
            <h1 className='mb-4'>Task Manager</h1>

            {/* Input and Search Box */}

            <div className='d-flex justify-content-between align-items-center mb-4 w-100'>
                <div className='input-group flex-grow-1 me-2'>
                    <input value={input} onChange={(e) => setInput(e.target.value)} type="text" placeholder='Add a new Task' className='form-control mw-1' />
                    <button onClick={handleTask} className='btn btn-success btn-sm me-2'>
                        <FaPlus className='m-2' />
                    </button>
                </div>
                <div className='input-group flex-grow-1'>
                    <span className='input-group-text'>
                        <FaSearch />
                    </span>
                    <input onChange={handleSearch} type="text" placeholder='Search Task' className='form-control mw-1' />
                </div>
            </div>

            {/* Lists of items */}

            <div className='d-flex flex-column w-100'>
                {
                    tasks.map((item) => (
                        <div key={item._id} className="m-2 p-2 border bg-light w-100 rounded-3 d-flex justify-content-between align-items-center flex-wrap">
                            <span className={item.isDone ? 'text-decoration-line-through' : ''}>{item.taskName}</span>
                            <div className="mt-2 mt-sm-0">
                                <button onClick={() => handleCheckAndUncheck(item)}
                                    className='btn btn-success btn-sm me-2' type='button'>
                                    <FaCheck />
                                </button>
                                <button onClick={() => setUpdateTask(item)}
                                    className='btn btn-primary btn-sm me-2' type='button'>
                                    <FaPenAlt />
                                </button>
                                <button onClick={() => handleDeleteTask(item._id)}
                                    className='btn btn-danger btn-sm me-2' type='button'>
                                    <FaTrash />
                                </button>
                            </div>
                        </div>
                    ))
                }
            </div>

            {/* Toastify */}

            <ToastContainer position='top-right' autoClose={3000} hideProgressBar={false} />
        </div>
    )
}
export default TaskManager;
